  ) : XdrEncodable {

  override fun toXdr(stream: XdrDataOutputStream) {
    txSetHash.toXdr(stream)
    closeTime.toXdr(stream)
    upgrades.size.toXdr(stream)
    upgrades.forEach {
      it.toXdr(stream)
    }
    ext.toXdr(stream)
  }

  abstract class StellarValueExt(val discriminant: LedgerVersion): XdrEncodable {
    override fun toXdr(stream: XdrDataOutputStream) {
