// === xdr source ============================================================

//  struct Transaction
//  {
//      // account used to run the transaction
//      AccountID sourceAccount;
//  
//      Salt salt;
//  
//      // validity range (inclusive) for the last ledger close time
//      TimeBounds timeBounds;
//  
//      Memo memo;
//  
//      Operation operations<100>;
//  
//      // reserved for future use
//      union switch (LedgerVersion v)
//      {
//      case EMPTY_VERSION:
//          void;
//      case ADD_TRANSACTION_FEE:
//          uint64 maxTotalFee;
//      }
//      ext;
//  };

//  ===========================================================================
open class Transaction(
    var sourceAccount: AccountID,
    var salt: Salt,
    var timeBounds: TimeBounds,
    var memo: Memo,
    var operations: Array<Operation>,
    var ext: TransactionExt
  ) : XdrEncodable {

  override fun toXdr(stream: XdrDataOutputStream) {
    sourceAccount.toXdr(stream)
    salt.toXdr(stream)
    timeBounds.toXdr(stream)
    memo.toXdr(stream)
    operations.size.toXdr(stream)
    operations.forEach {
      it.toXdr(stream)
    }
    ext.toXdr(stream)
  }

  abstract class TransactionExt(val discriminant: LedgerVersion): XdrEncodable {
    override fun toXdr(stream: XdrDataOutputStream) {
        discriminant.toXdr(stream)
    }

    open class EmptyVersion: TransactionExt(LedgerVersion.EMPTY_VERSION)

    open class AddTransactionFee(var maxTotalFee: Uint64): TransactionExt(LedgerVersion.ADD_TRANSACTION_FEE) {
      override fun toXdr(stream: XdrDataOutputStream) {
        super.toXdr(stream)
        maxTotalFee.toXdr(stream)
      }
    }
  }
}

