public enum class OperationResultCode(val value: Int): XdrEncodable {
  opINNER(0),
  opBAD_AUTH(-1),
  opNO_ACCOUNT(-2),
  opNOT_ALLOWED(-3),
  opACCOUNT_BLOCKED(-4),
  opNO_COUNTERPARTY(-5),
  opCOUNTERPARTY_BLOCKED(-6),
  opCOUNTERPARTY_WRONG_TYPE(-7),
  opBAD_AUTH_EXTRA(-8),
  ;

  override fun toXdr(stream: XdrDataOutputStream) {
      value.toXdr(stream)
  }
}

